import { useDispatch } from 'react-redux';
import { deleteTask } from '../features/tasks/taskSlice';
const TaskItem = ({ task }) => {
    return (
        <div className='task'>
            <div>{new Date(task.createdAt).toLocaleString('en-US')}</div>
            <h2>{task.text}</h2>
        </div>
        )
 }
export default TaskItem
